            <div className="w-2 h-2 bg-muted-foreground rounded-full"></div>
            <span className="text-sm text-muted-foreground">Free Plan</span>
          </div>
        </CardContent>
      </Card>
    );
  }

  const isActive = membership.expiresAt ? new Date(membership.expiresAt) > new Date() : false;
