import { Heart, Crown } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/contexts/auth-context";
import { type Membership } from "@shared/schema";

interface LikeButtonProps {
  songId: string;
  songTitle?: string;
  size?: "sm" | "default";
  className?: string;
}

export default function LikeButton({ 
  songId, 
  songTitle = "Song", 
  size = "sm",
  className = ""
}: LikeButtonProps) {
  const queryClient = useQueryClient();
  const { toast } = useToast();
  const { user } = useAuth();
  const userId = user?.uid;

  // Check user's membership status
  const { data: membership } = useQuery<Membership>({
    queryKey: ['/api/membership', userId],
    enabled: !!userId,
  });

  const isPremium = membership?.isPremium && membership.expiresAt && new Date(membership.expiresAt) > new Date();

  // Check if song is liked
  const { data: likeStatus } = useQuery<{ isLiked: boolean }>({
    queryKey: ['/api/liked-songs', songId, 'check'],
  });

  const isLiked = likeStatus?.isLiked || false;

  // Like/unlike mutation
  const likeMutation = useMutation({
    mutationFn: async () => {
      if (isLiked) {
        await apiRequest("DELETE", `/api/liked-songs/${songId}`);
      } else {
        await apiRequest("POST", "/api/liked-songs", { songId });
      }
    },
    onSuccess: () => {
      // Invalidate like status for this song
      queryClient.invalidateQueries({ 
        queryKey: ['/api/liked-songs', songId, 'check'] 
      });
      // Invalidate liked songs list
      queryClient.invalidateQueries({ 
        queryKey: ['/api/liked-songs'] 
      });

      toast({
        title: isLiked ? "Removed from Liked Songs" : "Added to Liked Songs",
        description: isLiked ? `${songTitle} removed from your liked songs` : `${songTitle} added to your liked songs`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update liked songs",
        variant: "destructive",
      });
    },
  });

  const handleClick = () => {
    if (!isPremium) {
      // Show premium required toast
      toast({
        title: "Premium Feature",
        description: "Liking songs is a premium feature. Upgrade to Premium for only 3.14Ï€ to unlock this and more features!",
        variant: "default",
        action: (
          <div className="flex items-center gap-1 text-primary">
            <Crown className="h-3 w-3" />
            <span className="text-xs">Upgrade</span>
          </div>
        ),
      });
      return;
    }

    // Proceed with like/unlike for premium users
    likeMutation.mutate();
  };

  return (
    <Button 
      variant="ghost" 
      size={size}
      onClick={handleClick}
      disabled={likeMutation.isPending}
      className={`transition-colors ${
        isLiked 
          ? 'text-red-500 hover:text-red-400' 
          : 'text-muted-foreground hover:text-primary'
      } ${className}`}
      data-testid={`button-like-song-${songId}`}
    >
      <Heart className={`h-4 w-4 ${isLiked ? 'fill-current' : ''}`} />
    </Button>
  );
}

client/src/components/playlist/create-pl.content:
import { useState } from "react";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { useAuth } from "@/contexts/auth-context";
import { Button } from "@/components/ui/button";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Plus, Crown, ImagePlus } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertPlaylistSchema, type Membership } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { z } from "zod";
import { ObjectUploader } from "@/components/object-uploader";

const createPlaylistFormSchema = insertPlaylistSchema.extend({
  name: z.string().min(1, "Playlist name is required").max(100, "Name too long"),
  description: z.string().max(500, "Description too long").optional(),
  imageUrl: z.string().optional(),
});

type CreatePlaylistFormData = z.infer<typeof createPlaylistFormSchema>;

interface CreatePlaylistDialogProps {
  trigger?: React.ReactNode;
}

export default function CreatePlaylistDialog({ trigger }: CreatePlaylistDialogProps) {
  const [open, setOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { userId } = useAuth();

  // Check user's membership status
  const { data: membership } = useQuery<Membership>({
    queryKey: ['/api/membership', userId],
    enabled: !!userId,
  });

  const isPremium = membership?.isPremium && membership.expiresAt && new Date(membership.expiresAt) > new Date();

  const form = useForm<CreatePlaylistFormData>({
    resolver: zodResolver(createPlaylistFormSchema),
    defaultValues: {
      name: "",
      description: "",
      imageUrl: "",
      isPublic: true,
      createdBy: userId || "",
    },
  });

  const createPlaylistMutation = useMutation({
    mutationFn: async (data: CreatePlaylistFormData) => {
      return await apiRequest("POST", "/api/playlists", data);
    },
    onSuccess: async (playlist) => {
      // Clear ALL cache
      queryClient.clear();

      toast({
        title: "Playlist Created", 
        description: "Playlist has been created successfully.",
      });

      // Reset form and close dialog
      form.reset();
      setOpen(false);

      // Force page reload after a short delay
      setTimeout(() => {
        window.location.reload();
      }, 1000);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "Failed to create playlist. Please try again.",
        variant: "destructive",
      });
      console.error("Failed to create playlist:", error);
    },
  });

  const onSubmit = (data: CreatePlaylistFormData) => {
    createPlaylistMutation.mutate(data);
  };

  const handleImageUpload = (uploadURL: string) => {
    // Convert upload URL to object path
    const objectPath = uploadURL.replace('https://storage.googleapis.com/', '/objects/');
    form.setValue('imageUrl', objectPath);
    toast({
      title: "Image Uploaded",
      description: "Playlist image has been uploaded successfully.",
    });
  };

  const defaultTrigger = (
    <Button variant="ghost" size="sm" className="text-primary">
      <Plus className="h-5 w-5 mr-2" />
      New Playlist
    </Button>
  );

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild data-testid="button-create-playlist">
        {trigger || defaultTrigger}
      </DialogTrigger>
      <DialogContent className="sm:max-w-[425px]">
        {!isPremium ? (
          // Premium Required Dialog
          <>
            <DialogHeader>
              <DialogTitle className="flex items-center gap-2">
                <Crown className="h-5 w-5 text-primary" />
                Premium Feature
              </DialogTitle>
              <DialogDescription>
                Creating custom playlists is a premium feature.
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div className="text-center p-6 bg-gradient-to-br from-primary/10 to-accent/10 rounded-lg">
                <Crown className="h-12 w-12 text-primary mx-auto mb-4" />
                <h3 className="text-lg font-semibold mb-2">Upgrade to Premium</h3>
                <p className="text-sm text-muted-foreground mb-4">
                  Get access to unlimited playlist creation, premium audio controls, and more exclusive features.
                </p>
                <p className="text-lg font-bold text-primary">
                  Only 3.14Ï€
                </p>
              </div>
              <div className="flex justify-end space-x-2">
                <Button 
                  variant="outline" 
                  onClick={() => setOpen(false)}
                  data-testid="button-cancel-premium"
                >
                  Maybe Later
                </Button>
                <Button 
                  className="bg-gradient-to-r from-primary to-accent text-white"
                  onClick={() => {
                    setOpen(false);
                    // TODO: Navigate to premium payment page
                  }}
                  data-testid="button-upgrade-premium"
                >
                  Upgrade Now
                </Button>
              </div>
            </div>
          </>
        ) : (
          // Regular Create Playlist Dialog for Premium Users
          <>
            <DialogHeader>
              <DialogTitle>Create New Playlist</DialogTitle>
              <DialogDescription>
                Create a new playlist to organize your favorite songs.
              </DialogDescription>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Playlist Name *</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="My Awesome Playlist" 
                          {...field} 
                          data-testid="input-playlist-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Image Upload Field */}
                <div className="space-y-2">
                  <FormLabel>Playlist Picture (optional)</FormLabel>
                  <div className="flex items-center space-x-4">
                    {form.watch('imageUrl') && (
                      <div className="w-20 h-20 bg-muted rounded-lg flex items-center justify-center overflow-hidden">
                        <img 
                          src={form.watch('imageUrl')} 
                          alt="Playlist cover" 
                          className="w-full h-full object-cover"
                        />
                      </div>
                    )}
                    <ObjectUploader
                      onComplete={handleImageUpload}
                      maxFileSize={5242880} // 5MB
                      buttonClassName="flex items-center gap-2"
                    >
                      <ImagePlus className="h-4 w-4" />
                      {form.watch('imageUrl') ? 'Change Picture' : 'Add Picture'}
                    </ObjectUploader>
                  </div>
                  <p className="text-xs text-muted-foreground">
                    Upload a cover image for your playlist (max 5MB)
                  </p>
                </div>

                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description (optional)</FormLabel>
                      <FormControl>
                        <Textarea 
                          placeholder="Describe your playlist..." 
                          rows={3}
                          {...field} 
                          data-testid="input-playlist-description"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="isPublic"
                  render={({ field }) => (
                    <FormItem className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
                      <div className="space-y-0.5">
                        <FormLabel>Public Playlist</FormLabel>
                        <div className="text-[0.8rem] text-muted-foreground">
                          Make this playlist visible to other users
                        </div>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value || false}
                          onCheckedChange={field.onChange}
                          data-testid="switch-playlist-public"
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2 pt-4">
                  <Button 
                    type="button" 
                    variant="outline" 
                    onClick={() => setOpen(false)}
                    data-testid="button-cancel-playlist"
                  >
                    Cancel
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={createPlaylistMutation.isPending}
                    data-testid="button-submit-playlist"
                  >
                    {createPlaylistMutation.isPending ? "Creating..." : "Create Playlist"}
                  </Button>
                </div>
              </form>
            </Form>
          </>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/music/song-options.content:
import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/contexts/auth-context";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuSub,
  DropdownMenuSubContent,
  DropdownMenuSubTrigger,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Button } from "@/components/ui/button";
import { Heart, Plus, MoreHorizontal, Music, Crown, Lock } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { type SongWithDetails, type Playlist, type Membership } from "@shared/schema";

interface SongOptionsMenuProps {
  song: SongWithDetails;
  className?: string;
}

export default function SongOptionsMenu({ song, className = "" }: SongOptionsMenuProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user } = useAuth();
  const userId = user?.uid;

  // Get user's membership status
  const { data: membership } = useQuery<Membership>({
    queryKey: ['/api/membership', userId],
    enabled: !!userId,
  });

  const isPremium = membership?.isPremium && membership.expiresAt && new Date(membership.expiresAt) > new Date();

  // Check if song is liked
  const { data: likeStatus } = useQuery<{ isLiked: boolean }>({
    queryKey: ['/api/liked-songs', song.id, 'check'],
  });

  // Get all playlists for adding song
  const { data: allPlaylists } = useQuery<Playlist[]>({
    queryKey: ["/api/playlists"],
  });

  // Get user's created playlists
  const { data: userPlaylists } = useQuery<Playlist[]>({
    queryKey: ["/api/playlists/user", userId],
    enabled: !!userId,
  });

  // Combine all playlists and user playlists, removing duplicates
  const playlists = React.useMemo(() => {
    const combined = [...(allPlaylists || []), ...(userPlaylists || [])];
    // Remove duplicates by id
    const uniquePlaylists = combined.filter((playlist, index, self) => 
      index === self.findIndex(p => p.id === playlist.id)
    );
    return uniquePlaylists;
  }, [allPlaylists, userPlaylists]);

  const isLiked = likeStatus?.isLiked || false;

  // Like/unlike mutation
  const likeMutation = useMutation({
    mutationFn: async () => {
      if (!isPremium) {
        throw new Error("Premium feature required. Upgrade to Premium to like songs!");
      }
      if (isLiked) {
        await apiRequest("DELETE", `/api/liked-songs/${song.id}`);
      } else {
        await apiRequest("POST", "/api/liked-songs", { songId: song.id });
      }
    },
    onSuccess: () => {
      // Invalidate like status for this song
      queryClient.invalidateQueries({ 
        queryKey: ['/api/liked-songs', song.id, 'check'] 
      });
      // Invalidate liked songs list
      queryClient.invalidateQueries({ 
        queryKey: ['/api/liked-songs'] 
      });

      toast({
        title: isLiked ? "Removed from Liked Songs" : "Added to Liked Songs",
        description: isLiked ? `${song.title} removed from your liked songs` : `${song.title} added to your liked songs`,
      });
    },
    onError: (error: any) => {
      console.error("Like song error:", error);
      const message = error?.message || "Failed to update liked songs";
      if (message.includes("Premium feature required")) {
        toast({
          title: "Premium Feature",
          description: "Liking songs is a premium feature. Upgrade to Premium for only 3.14Ï€!",
          variant: "default",
          action: (
            <div className="flex items-center gap-1 text-primary">
              <Crown className="h-3 w-3" />
              <span className="text-xs">Upgrade</span>
            </div>
          ),
        });
      } else {
        toast({
          title: "Error",
          description: message,
          variant: "destructive",
        });
      }
    },
  });

  // Add to playlist mutation
  const addToPlaylistMutation = useMutation({
    mutationFn: async (playlistId: string) => {
      // Get playlist details to find next position
      const playlistResponse = await apiRequest("GET", `/api/playlists/${playlistId}`);
      const playlist = await playlistResponse.json();
      const nextPosition = (playlist.songs?.length || 0) + 1;

      await apiRequest("POST", `/api/playlists/${playlistId}/songs`, {
        songId: song.id,
        position: nextPosition,
      });
    },
    onSuccess: (_, playlistId) => {
      // Invalidate playlist data to show new song
      queryClient.invalidateQueries({ 
        queryKey: ["/api/playlists", playlistId] 
      });
      queryClient.invalidateQueries({ 
        queryKey: ["/api/playlists"] 
      });
      // Invalidate user playlists to update song count on profile  
      queryClient.removeQueries({ 
        queryKey: ["/api/playlists/user", "demo-user-123"] 
      });
      queryClient.invalidateQueries({ 
        queryKey: ["/api/playlists/user"] 
      });

      toast({
        title: "Added to Playlist",
        description: `${song.title} has been added to the playlist.`,
      });
    },
    onError: (error: any) => {
      console.error("Add to playlist error:", error);
      const message = error?.message || "Failed to add song to playlist";
      if (message.includes("Premium feature required")) {
        toast({
          title: "Premium Feature",
          description: "Adding songs to playlists is a premium feature. Upgrade to Premium for only 3.14Ï€!",
          variant: "default",
          action: (
            <div className="flex items-center gap-1 text-primary">
              <Crown className="h-3 w-3" />
              <span className="text-xs">Upgrade</span>
            </div>
          ),
        });
      } else {
        toast({
          title: "Error",
          description: message,
          variant: "destructive",
        });
      }
    },
  });

  const handleAddToPlaylist = (playlistId: string) => {
    addToPlaylistMutation.mutate(playlistId);
  };

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button 
          variant="ghost" 
          size="sm"
          className={`p-2 text-muted-foreground hover:text-foreground ${className}`}
          data-testid={`button-song-options-${song.id}`}
        >
          <MoreHorizontal className="h-4 w-4" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent className="w-56" align="end">
        <DropdownMenuLabel>Song Options</DropdownMenuLabel>
        <DropdownMenuSeparator />

        {/* Like/Unlike Option */}
        <DropdownMenuItem 
          onClick={() => {
            if (!isPremium) {
              toast({
                title: "Premium Feature",
                description: "Liking songs is a premium feature. Upgrade to Premium for only 3.14Ï€!",
                variant: "default",
                action: (
                  <div className="flex items-center gap-1 text-primary">
                    <Crown className="h-3 w-3" />
                    <span className="text-xs">Upgrade</span>
                  </div>
                ),
              });
              return;
            }
            likeMutation.mutate();
          }}
          disabled={likeMutation.isPending}
          className={`cursor-pointer ${!isPremium ? 'opacity-50' : ''}`}
          data-testid={`option-like-${song.id}`}
        >
          {!isPremium ? (
            <Lock className="mr-2 h-4 w-4 text-muted-foreground" />
          ) : (
            <Heart className={`mr-2 h-4 w-4 ${isLiked ? 'fill-current text-red-500' : ''}`} />
          )}
          {!isPremium ? "Add to Liked Songs" : (isLiked ? "Remove from Liked Songs" : "Add to Liked Songs")}
          {!isPremium && <Crown className="ml-auto h-3 w-3 text-primary" />}
        </DropdownMenuItem>

        <DropdownMenuSeparator />

        {/* Add to Playlist Submenu */}
        <DropdownMenuSub>
          <DropdownMenuSubTrigger 
            className={`${!isPremium ? 'opacity-50' : ''}`}
            data-testid={`option-add-to-playlist-${song.id}`}
          >
            {!isPremium ? (
              <Lock className="mr-2 h-4 w-4 text-muted-foreground" />
            ) : (
              <Plus className="mr-2 h-4 w-4" />
            )}
            Add to Playlist
            {!isPremium && <Crown className="ml-auto h-3 w-3 text-primary" />}
          </DropdownMenuSubTrigger>
          <DropdownMenuSubContent>
            {!isPremium ? (
              <DropdownMenuItem 
                onClick={() => {
                  toast({
                    title: "Premium Feature",
                    description: "Adding songs to playlists is a premium feature. Upgrade to Premium for only 3.14Ï€!",
                    variant: "default",
                    action: (
                      <div className="flex items-center gap-1 text-primary">
                        <Crown className="h-3 w-3" />
                        <span className="text-xs">Upgrade</span>
                      </div>
                    ),
                  });
                }}
                className="cursor-pointer opacity-50"
              >
                <Lock className="mr-2 h-4 w-4 text-muted-foreground" />
                Premium Required
                <Crown className="ml-auto h-3 w-3 text-primary" />
              </DropdownMenuItem>
            ) : (
              <>
                {playlists && playlists.length > 0 ? (
                  playlists.map((playlist) => (
                    <DropdownMenuItem
                      key={playlist.id}
                      onClick={() => handleAddToPlaylist(playlist.id)}
                      disabled={addToPlaylistMutation.isPending}
                      className="cursor-pointer"
                      data-testid={`option-playlist-${playlist.id}`}
                    >
                      <Music className="mr-2 h-4 w-4" />
                      {playlist.name}
                    </DropdownMenuItem>
                  ))
                ) : (
                  <DropdownMenuItem disabled>
                    <Music className="mr-2 h-4 w-4" />
                    No playlists available
                  </DropdownMenuItem>
                )}
              </>
            )}
          </DropdownMenuSubContent>
        </DropdownMenuSub>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

